DROP VIEW IF EXISTS "AllChecksAsserts";
CREATE TEMP VIEW AllChecksAsserts AS SELECT "Id" as "Id", "Checker Name" as "Checker Name", "Analysis Name" as "Analysis Name", 'DeviceChecks' as [Type], "Instance" as "Inst/Net", 'simInst' as [DesignObjectType], "StartTime (s)" as "Time (s)", "Duration (s)" as "Duration (s)" FROM DeviceChecks UNION ALL SELECT "Id" as "Id", "Checker Name" as "Checker Name", "Analysis Name" as "Analysis Name", "Type" as "Type", "Inst/Net" as "Inst/Net", "DesignObjectType" as "DesignObjectType", "Time (s)" as "Time (s)", "Duration (s)" as "Duration (s)" FROM DynamicChecks UNION ALL SELECT "Id" as "Id", "Checker Name" as "Checker Name", NULL as [Analysis Name], "Type" as "Type", "Inst/Net" as "Inst/Net", "DesignObjectType" as "DesignObjectType", NULL as [Time (s)], NULL as [Duration (s)] FROM StaticChecks UNION ALL SELECT "Id" as "Id", "Checker Name" as "Checker Name", "Analysis Name" as "Analysis Name", "Type" as "Type", "Inst/Net" as "Inst/Net", "DesignObjectType" as "DesignObjectType", "Time (s)" as "Time (s)", "Duration (s)" as "Duration (s)" FROM Aux;

DROP VIEW IF EXISTS "AllChecksAssertsSummary";
CREATE TEMP VIEW AllChecksAssertsSummary AS SELECT min("Id") as Id, "Checker Name", "Type", "Analysis Name", "Inst/Net", "DesignObjectType", count(*) as Count, min("Time (s)") as "First Time (s)", sum("Duration (s)") as "Cumulative Duration (s)" FROM AllChecksAsserts GROUP BY "Checker Name", "Analysis Name", "Inst/Net", "DesignObjectType";

